mapper.insert:
INSERT INTO tb_comment (

) VALUES (
	  #{idx}
	, #{boardIdx}
	, #{content}
	, #{writer}
	, IFNULL(#{deleteYn}, 'N')
	, NOW()
	, NULL
	, NULL
)

mapper.update:
UPDATE tb_comment
SET
	  last_update_time = NOW()
	, version_name = #{content}
	, version_code = #{writer}
WHERE
	idx = #{idx}
